\n        }\r\n        \r\n        $payment_log = $db->PaymentApiLog->findbyid($c_order);\r\n        \r\n        //验证通过后,将订单sn转换为ID 来操作ec订单表\r\n      /*  if ($c_memo2 == ''voucher'')\r\n        {\r\n            $c_order = get_order_id_by_sn($c_order, "true");\r\n        }\r\n        else\r\n        {\r\n            $c_order = get_order_id_by_sn($c_order);\r\n        }*/\r\n		if($payment_log[''PaymentApiLog''][''type''] > 0){\r\n			$account = $db->UserAccount->findbyid($payment_log[''PaymentApiLog''][''type_id'']);\r\n	        if ($c_orderamount != $account[''UserAccount''][''amount''])\r\n	        {\r\n	            return false;\r\n	        }\r\n        }else{\r\n        $order_total = $db->Order->findbyid($payment_log[''PaymentApiLog''][''type_id'']);\r\n	        if ($c_orderamount != $order_total[''Order''][''total''])\r\n	        {\r\n	            return false;\r\n	        }\r\n        }\r\n\r\n\r\n        //--校验商户编号\r\n        $MerchantID= trim($c_mid);   //商户自己的编号\r\n        if($MerchantID!=$c_mid){\r\n            //echo "提交的商户编号有误";\r\n\r\n            return false;\r\n        }\r\n\r\n        if ($c_memo1 != abs(crc32($payment_arr[''c_memo1''][''value''])))\r\n        {\r\n            //echo "个性签名不一致";\r\n            //return false;\r\n        }\r\n\r\n        //--校验返回的支付结果的格式是否正确\r\n        if($c_succmark!="Y" && $c_succmark!="N")\r\n        {\r\n            //echo "参数提交有误";\r\n\r\n            return false;\r\n        }\r\n\r\n        //--根据返回的支付结果，商户进行自己的发货等操作\r\n        if($c_succmark="Y")\r\n        {\r\n            //根据商户自己商务规则，进行发货等系列操作\r\n\r\n		if($payment_log[''PaymentApiLog''][''type''] > 0){\r\n			/* 充值成功*/\r\n			$db->update_balance($payment_log,$order_total);      \r\n			   return true;\r\n		}else {\r\n            /* 改变订单状态 */\r\n			$db->update_order($payment_log,$order_total);       \r\n			 return true;\r\n			}\r\n        }\r\n        else\r\n        {\r\n            //echo $c_cause;\r\n\r\n            return false;\r\n        }\r\n    }\r\n    \r\n    function local_date($format,$db,$time = NULL)\r\n	{\r\n	    $timezone = $db->locale[''default_timezone'']*(-1);\r\n\r\n	    if ($time === NULL)\r\n	    {\r\n	        $time = gmtime();\r\n	    }\r\n	    elseif ($time <= 0)\r\n	    {\r\n	        return '''';\r\n	    }\r\n\r\n	    $time += ($timezone * 3600);\r\n\r\n	    return date($format, $time);\r\n	}    \r\n    \r\n}', '', '2008-01-01 00:00:00', '2009-07-16 08:51:23'),
