(14, 0, 'googlecheckout', '0', 0, '$payment_arr = array(''merchant_id''=> array(''name''=> ''商户编号'',''value''=>'''' , ''type'' => ''text''),''merchant_key''=> array(''name''=> ''支付密钥'',''value''=>'''' , ''type'' => ''text''));', '0', '0', '0', '1', '1', 'class googlecheckout\r\n{\r\n    function googlecheckout()\r\n    {\r\n    }\r\n    \r\n    function __construct()\r\n    {\r\n        $this->googlecheckout();\r\n    }\r\n    \r\n    function get_code($order,$payment,$db)\r\n    {\r\n		$host = isset($_SERVER[''HTTP_X_FORWARDED_HOST'']) ? $_SERVER[''HTTP_X_FORWARDED_HOST''] : (isset($_SERVER[''HTTP_HOST'']) ? $_SERVER[''HTTP_HOST''] : '''');\r\n        eval($payment[''Payment''][''config'']); \r\n		$merchant_id = $payment_arr[''merchant_id''][''value''];\r\n		$merchant_key = $payment_arr[''merchant_key''][''value''];\r\n        \r\n        $server_type = "sandbox";\r\n    	$currency = "USD";\r\n      	$cart = new GoogleCart($merchant_id, $merchant_key, $server_type,$currency);\r\n      	$total_count = 1;\r\n		$item_1 = new GoogleItem("item name",      // Item name\r\n                               $db->configs[''shop_name'']."-".$order[''log_id''], 		// Item      description\r\n                               $total_count, 		// Quantity\r\n                              $order[''total'']); 	// Unit price      \r\n        $cart->AddItem($item_1);\r\n        $ship_1 = new GoogleFlatRateShipping("USPS Priority Mail", 4.55);\r\n      	$Gfilter = new GoogleShippingFilters();\r\n      	$Gfilter->SetAllowedCountryArea(''CONTINENTAL_48'');    	\r\n		$ship_1->AddShippingRestrictions($Gfilter);\r\n        $cart->AddShipping($ship_1);    	\r\n		// Add tax rules\r\n	    $tax_rule = new GoogleDefaultTaxRule(0.05);\r\n	    $tax_rule->SetStateAreas(array("MA"));\r\n	    $cart->AddDefaultTaxRules($tax_rule);\r\n	    // Specify <edit-cart-url>\r\n	    $cart->SetEditCartUrl("http://".$host.$db->webroot."/responds/index/googlecheckout");\r\n	    // Specify "Return to xyz" link\r\n	    $cart->SetContinueShoppingUrl("http://".$host.$db->webroot."/responds/index/googlecheckout");\r\n	    // Request buyer''s phone number\r\n	    $cart->SetRequestBuyerPhone(true);\r\n	    // Display Google Checkout button\r\n	    return $cart->CheckoutButtonCode("SMALL");\r\n    }\r\n\r\n    function respond($db)\r\n    {\r\n			$payment = $db->Payment->findbycode(''googlecheckout'');\r\n		    eval($payment[''Payment''][''config'']);\r\n			$merchant_id = $payment_arr[''merchant_id''][''value''];  // Your Merchant ID\r\n			$merchant_key = $payment_arr[''merchant_key''][''value''];  // Your Merchant Key\r\n		    $server_type = "sandbox";  // change this to go live\r\n		    $currency = ''USD'';  // set to GBP if in the UK\r\n		    \r\n		    $Gresponse = new GoogleResponse($merchant_id, $merchant_key);\r\n		    $Grequest = new GoogleRequest($merchant_id, $merchant_key, $server_type, $currency);\r\n		    $xml_response = isset($HTTP_RAW_POST_DATA)?\r\n		                    $HTTP_RAW_POST_DATA:file_get_contents("php://input");\r\n		      if (get_magic_quotes_gpc()) {\r\n			    $xml_response = stripslashes($xml_response);\r\n			  }\r\n			  list($root, $data) = $Gresponse->GetParsedXML($xml_response);\r\n			  $Gresponse->SetMerchantAuthentication($merchant_id, $merchant_key);\r\n			  $status = $Gresponse->HttpAuthentication();\r\n			  if(!$status) {\r\n			    die(''authentication failed'');\r\n			  }\r\n		    \r\n		  switch ($root) {\r\n		    case "request-received": {\r\n		      break;\r\n		    }\r\n		    case "error": {\r\n		      break;\r\n		    }\r\n		    case "diagnosis": {\r\n		      break;\r\n		    }\r\n		    case "checkout-redirect": {\r\n		      break;\r\n		    }\r\n		    case "merchant-calculation-callback": {\r\n		      $merchant_calc = new GoogleMerchantCalculations($currency);\r\n		      $addresses = get_arr_result($data[$root][''calculate''][''addresses''][''anonymous-address'']);\r\n		      foreach($addresses as $curr_address) {\r\n		        $curr_id = $curr_address[''id''];\r\n		        $country = $curr_address[''country-code''][''VALUE''];\r\n		        $city = $curr_address[''city''][''VALUE''];\r\n		        $region = $curr_address[''region''][''VALUE''];\r\n		        $postal_code = $curr_address[''postal-code''][''VALUE''];\r\n		        if(isset($data[$root][''calculate''][''shipping''])) {\r\n		          $shipping = get_arr_result($data[$root][''calculate''][''shipping''][''method'']);\r\n		          foreach($shipping as $curr_ship) {\r\n		            $name = $curr_ship[''name''];\r\n		            $price = 12; // Modify this to get the actual price\r\n		            $shippable = "true"; // Modify this as required\r\n		            $merchant_result = new GoogleResult($curr_id);\r\n		            $merchant_result->SetShippingDetails($name, $price, $shippable);\r\n\r\n		            if($data[$root][''calculate''][''tax''][''VALUE''] == "true") {\r\n		              $amount = 15; // Modify this to the actual tax value\r\n		              $merchant_result->SetTaxDetails($amount);\r\n		            }\r\n\r\n		            if(isset($data[$root][''calculate''][''merchant-code-strings'']\r\n		                [''merchant-code-string''])) {\r\n		              $codes = get_arr_result($data[$root][''calculate''][''merchant-code-strings'']\r\n		                  [''merchant-code-string'']);\r\n		              foreach($codes as $curr_code) {\r\n		                //Update this data as required to set whether the coupon is valid, the code and the amount\r\n		                $coupons = new GoogleCoupons("true", $curr_code[''code''], 5, "test2");\r\n		                $merchant_result->AddCoupons($coupons);\r\n		              }\r\n		             }\r\n		             $merchant_calc->AddResult($merchant_result);\r\n		          }\r\n		        } else {\r\n		          $merchant_result = new GoogleResult($curr_id);\r\n		          if($data[$root][''calculate''][''tax''][''VALUE''] == "true") {\r\n		            //Compute tax for this address id and shipping type\r\n		            $amount = 15; // Modify this to the actual tax value\r\n		            $merchant_result->SetTaxDetails($amount);\r\n		          }\r\n		          $codes = get_arr_result($data[$root][''calculate''][''merchant-code-strings'']\r\n		              [''merchant-code-string'']);\r\n		          foreach($codes as $curr_code) {\r\n		            //Update this data as required to set whether the coupon is valid, the code and the amount\r\n		            $coupons = new GoogleCoupons("true", $curr_code[''code''], 5, "test2");\r\n		            $merchant_result->AddCoupons($coupons);\r\n		          }\r\n		          $merchant_calc->AddResult($merchant_result);\r\n		        }\r\n		      }\r\n		      $Gresponse->ProcessMerchantCalculations($merchant_calc);\r\n		      break;\r\n		    }\r\n		    case "new-order-notification": {\r\n		      $Gresponse->SendAck();\r\n		      break;\r\n		    }\r\n		    case "order-state-change-notification": {\r\n		      $Gresponse->SendAck();\r\n		      $new_financial_state = $data[$root][''new-financial-order-state''][''VALUE''];\r\n		      $new_fulfillment_order = $data[$root][''new-fulfillment-order-state''][''VALUE''];\r\n\r\n		      switch($new_financial_state) {\r\n		        case ''REVIEWING'': {\r\n		          break;\r\n		        }\r\n		        case ''CHARGEABLE'': {\r\n		          break;\r\n		        }\r\n		        case ''CHARGING'': {\r\n		          break;\r\n		        }\r\n		        case ''CHARGED'': {\r\n		          break;\r\n		        }\r\n		        case ''PAYMENT_DECLINED'': {\r\n		          break;\r\n		        }\r\n		        case ''CANCELLED'': {\r\n		          break;\r\n		        }\r\n		        case ''CANCELLED_BY_GOOGLE'': {\r\n		          break;\r\n		        }\r\n		        default:\r\n		          break;\r\n		      }\r\n\r\n		      switch($new_fulfillment_order) {\r\n		        case ''NEW'': {\r\n		          break;\r\n		        }\r\n		        case ''PROCESSING'': {\r\n		          break;\r\n		        }\r\n		        case ''