(8, 0, 'paypal', '1%', 0, '$payment_arr = array(''data_pay_account''=> array(''name''=> ''贝宝帐户'',''value''=>'''' , ''type'' => ''text''),''currency_code''=> array(''name''=> ''货币'',''value''=>''USD'' , ''type'' => ''select'', ''select_value'' => array( ''澳元'' => ''AUD'',''加元'' => ''CAD'',''欧元'' => ''EUR'',''英镑'' => ''GBP'',''日元'' => ''JPY'',''美元'' => ''USD'',''港元'' => ''HKD'')),''languages_type''=> array(''name''=> ''按语言选择货币'' , ''type'' => ''all_select'',''value''=>array(''eng'' => array( ''name''=> ''English'' , ''value'' => ''0''),''chi'' => array( ''name''=> ''中文'' , ''value'' => ''0''),''jpn'' => array( ''name''=> ''日本語'' , ''value'' => ''0''),''fre'' => array( ''name''=> ''Français'' , ''value'' => ''0''))));', '1', '1', '1', '1', '1', 'class paypal\r\n{\r\n    function paypal()\r\n    {\r\n    }\r\n    function __construct()\r\n    {\r\n        $this->paypal();\r\n    }\r\n    function get_code($order, $payment,$db)\r\n    {\r\n\r\n	$host = isset($_SERVER[''HTTP_X_FORWARDED_HOST'']) ? $_SERVER[''HTTP_X_FORWARDED_HOST''] : (isset($_SERVER[''HTTP_HOST'']) ? $_SERVER[''HTTP_HOST''] : '''');\r\n		eval($payment[''Payment''][''config'']);\r\n		$data_pay_account   = $payment_arr[''data_pay_account''][''value''];\r\n		if(isset($db->configs[''mlti_currency_module'']) && $db->configs[''mlti_currency_module''] == 1 && isset($order[''currency_code''])){\r\n			$currency_code      = $order[''currency_code''];\r\n		}else{\r\n			$currency_code      = $payment_arr[''currency_code''][''value''];\r\n		}\r\n        $data_order_id      = $order[''log_id''];\r\n		$no_note 	   		= $db->configs[''shop_name'']."-".$order[''log_id''];\r\n        $data_amount        = $order[''total''];\r\n        $data_return_url    = "http://".$host.$db->webroot."responds/index/paypal";\r\n        $data_notify_url    = "http://".$host.$db->webroot."responds/index/paypal";\r\n        $cancel_return      = "http://".$host.$db->webroot."";\r\n\r\n        $def_url  = ''<br /><form style="text-align:center;" action="https://www.paypal.com/cgi-bin/webscr" method="post" target="_blank">'' .   // 不能省略\r\n            "<p class=''back_home''>".\r\n	    "<input type=''hidden'' name=''cmd'' value=''_xclick''>" .                             // 不能省略\r\n            "<input type=''hidden'' name=''business'' value=''$data_pay_account''>" .                 // 贝宝帐号\r\n            "<input type=''hidden'' name=''item_name'' value=''$no_note''>" .                 // payment for\r\n            "<input type=''hidden'' name=''amount'' value=''$data_amount''>" .                        // 订单金额\r\n            "<input type=''hidden'' name=''currency_code'' value=''$currency_code''>" .            // 货币\r\n            "<input type=''hidden'' name=''return'' value=''$data_return_url''>" .                    // 付款后页面\r\n            "<input type=''hidden'' name=''invoice'' value=''$data_order_id''>" .                      // 订单号\r\n            "<input type=''hidden'' name=''charset'' value=''utf-8''>" .                              // 字符集\r\n            "<input type=''hidden'' name=''no_shipping'' value=''1''>" .                              // 不要求客户提供收货地址\r\n            "<input type=''hidden'' name=''no_note'' value=''$no_note''>" .                                  // 付款说明\r\n            "<input type=''hidden'' name=''notify_url'' value=''$data_notify_url''>" .\r\n            "<input type=''hidden'' name=''rm'' value=''2''>" .\r\n            "<input type=''hidden'' name=''cancel_return'' value=''$cancel_return''>" .\r\n            "<input type=''submit'' value=''使用 paypal 支付''>" .                      // 按钮\r\n            "</p>".\r\n		"</form><br />";\r\n\r\n        return $def_url;\r\n    }\r\n    function respond($db)\r\n    {\r\n        \r\n        \r\n        //$payment        = get_payment(''paypal'');\r\n        //$merchant_id    = $payment[''paypal_account''];               ///获取商户编号\r\n        $payment = $db->Payment->findbycode(''paypal'');\r\n		eval($payment[''Payment''][''config'']);\r\n		$merchant_id   = $payment_arr[''data_pay_account''][''value''];\r\n		$currency_code      = $payment_arr[''currency_code''][''value''];        \r\n		// read the post from PayPal system and add ''cmd''\r\n        $req = ''cmd=_notify-validate'';\r\n        foreach ($_POST as $key => $value)\r\n        {\r\n            $value = urlencode(stripslashes($value));\r\n            $req .= "&$key=$value";\r\n        }\r\n\r\n        // post back to PayPal system to validate\r\n        $header = "POST /cgi-bin/webscr HTTP/1.0\\r\\n";\r\n        $header .= "Content-Type: application/x-www-form-urlencoded\\r\\n";\r\n        $header .= "Content-Length: " . strlen($req) ."\\r\\n\\r\\n";\r\n        $fp = fsockopen (''www.paypal.com'', 80, $errno, $errstr, 30);\r\n\r\n        // assign posted variables to local variables\r\n        $item_name = $_POST[''item_name''];\r\n        $item_number = $_POST[''item_number''];\r\n        $payment_status = $_POST[''payment_status''];\r\n        $payment_amount = $_POST[''mc_gross''];\r\n        $payment_currency = $_POST[''mc_currency''];\r\n        $txn_id = $_POST[''txn_id''];\r\n        $receiver_email = $_POST[''receiver_email''];\r\n        $payer_email = $_POST[''payer_email''];\r\n        $order_sn = $_POST[''invoice''];\r\n        $memo = !empty($_POST[''memo'']) ? $_POST[''memo''] : '''';\r\n        $action_note = $txn_id . ''（paypal 交易号）'' . $memo;\r\n\r\n        if (!$fp)\r\n        {\r\n            fclose($fp);\r\n\r\n            return false;\r\n        }\r\n        else\r\n        {\r\n            fputs($fp, $header . $req);\r\n            while (!feof($fp))\r\n            {\r\n                $res = fgets($fp, 1024);\r\n                if (strcmp($res, ''VERIFIED'') == 0)\r\n                {\r\n                    // check the payment_status is Completed\r\n                    if ($payment_status != ''Completed'' && $payment_status != ''Pending'')\r\n                    {\r\n                        fclose($fp);\r\n\r\n                        return false;\r\n                    }\r\n                    // check that receiver_email is your Primary PayPal email\r\n                    if ($receiver_email != $merchant_id)\r\n                    {\r\n                        fclose($fp);\r\n\r\n                        return false;\r\n                    }\r\n                    \r\n        			$payment_log = $db->PaymentApiLog->findbyid($order_sn);\r\n        					//检查是否已成功支付\r\n					if($payment_log[''PaymentApiLog''][''is_paid''] == "1"){\r\n			                fclose($fp);\r\n			 			return false;\r\n			 		}\r\n			        $order_total = $db->Order->findbyid($payment_log[''PaymentApiLog''][''type_id'']);\r\n                    // check that payment_amount/payment_currency are correct\r\n					if($payment_log[''PaymentApiLog''][''type''] > 0){\r\n					$account = $db->UserAccount->findbyid($payment_log[''PaymentApiLog''][''type_id'']);\r\n				        if ($payment_amount != $account[''UserAccount''][''amount''])\r\n				        {\r\n			                fclose($fp);\r\n				            return false;\r\n				        }\r\n			        }else{\r\n	        	        if ($order_total[''Order''][''total''] != $payment_amount)\r\n	                    {\r\n	                        fclose($fp);\r\n\r\n	                        return false;\r\n	                    }\r\n        			}\r\n					if(isset($db->configs[''mlti_currency_module'']) && $db->configs[''mlti_currency_module''] == 1){\r\n						$currency_code      = $order_total[''Order''][''currency_code''];\r\n					}\r\n					\r\n                    if ($currency_code != $payment_currency)\r\n                    {\r\n                        fclose($fp);\r\n\r\n                        return false;\r\n                    }\r\n                    \r\n	  \r\n				    if($payment_log[''PaymentApiLog''][''type''] < 1)\r\n				    {                    // process payment\r\n			            /* 改变订单状态 */\r\n						$db->update_order($payment_log,$order_total);        			           \r\n	                    fclose($fp);\r\n	                    return true;\r\n                    }else{\r\n						/* 充值成功*/\r\n						$db->update_balance($payment_log,$order_total);       \r\n						fclose($fp);\r\n				      	return true;\r\n                    }\r\n                }\r\n                elseif (strcmp($res, ''INVALID'') == 0)\r\n                {\r\n                    // log for manual investigation\r\n                    fclose($fp);\r\n\r\n                    return false;\r\n                }\r\n            }\r\n        }\r\n    }\r\n}\r\n', '', '2008-01-01 00:00:00', '2009-07-20 10:14:36'),
