INSERT INTO svcart_article_i18ns (id, locale, article_id, title, meta_keywords, meta_description, content, author, img01, img02, created, modified) VALUES
(1, 'chi', 1, '付款说明', '付款说明', '', '<p>付款说明：<br />\r\n1. 汇款时请多汇几元钱或者几角钱，如600.7元，目的是为了确认谁汇的款。<br />\r\n2. 付款后发EMAIL到：pay@studa.com，说明汇款日期、银行、金额、详细要求以及您的联系电话！<br />\r\n3. 九年相关业务成功运作经验，从没出现信誉问题，信用问题请查看信用说明。<br />\r\n4. 您有任何问题，请在7&times;8小时内通过服务热线:0737-2800345 2800007查询。<br />\r\n5. 您对服务人员的服务不满意，请投诉到站长信箱：studaa@gmail.com，站长24小时内一定给您解决。</p>', '', '', NULL, '2009-02-11 09:27:19', '2009-04-29 14:36:02'),
(2, 'chi', 2, '配送须知', '配送须知', '', '<p>1. 首先，您在易购商城进行购物时，请正确填写个人信息以便更好的享受我们的配送服务。<br />\r\n2.正常情况下，我们会在收到您货款的当日将您的货品发出。<br />\r\n3.一般情况下我们会以快递方式发出您的订单。72小时内您可以收到您的物品。国内大多数用户可以享受此服务。<br />\r\n4.对于极个别地区，我们可能会采用邮政方式寄出。收货时间大约在7－10天左右。对于此种方式，我们会在寄出前与您沟通。</p>\r\n<p>得到您的确认后，我们才会使用。<br />\r\n5.关于邮资收取，我们遵循以下原则：本埠（北京）单次交易100元以上免邮费，不足100元收取5元；外埠（不含港澳台）</p>\r\n<p>200元以上免邮费，不足200元收取10元。港澳台地区邮费按实际发生收取。</p>\r\n<p>6.由于委托第三方运输，请您务必在收到货物的第一时间查收，以确认您收到的物品数量和品质等与您的订单一致。如果有疑问，</p>\r\n<p>您可以在此时通知我们。如果您放弃这个权利，我们将视为您已经对收到的物品无任何异议。<br />\r\n7.我们不支持无条件退换货服务。如无质量问题，商品一经售出，将概不退货。所以为了最大限度的保证您的权益，在选购商品时，</p>\r\n<p>请您谨慎下单。</p>\r\n<p><br />\r\n感谢您阅读以上条款，祝您在本站购物愉快！<br />\r\n客服中心：（010）62195755</p>', '', '', NULL, '2009-02-11 09:27:19', '2009-04-30 11:36:27'),
(3, 'chi', 3, 'SV-Cart电子商务平台 v1.0 正式版发布了', '', '', '<p>免费版v1.0发布<br />\r\n功能介绍（包括免费版与商业版的功能对比）<br />\r\n免费、开源：SV-CART网上商城系统是一款开源免费的网上商城系统，是一套集网上购物和网站内容管理于一体的电子商务解决方案。<br />\r\n操作便捷、功能强劲：SV-CART系统注重操作上的便捷易用与功能上的强大完善，使您能方便自主的管理自己的网上店铺。前台：网上购物、会员管理、商品检 索等网上商店所需要的功能。后台：强大简便的网站内容管理功能，只需轻点鼠标、简单录入即可完成网上商城管理，如网站模板更换、编辑商品栏目、发货管理、 订单管理、批量导入数据功能、整合多种主流支付通道、内置多国语言等等。<br />\r\n性能好、速度快：SV-CART系统在网站速度上采用专业手段进行优化，速度快、性能好、操作便捷、安全性高。更可根据客户需求进行搜索引擎优化，使您在GOOGLE、BAIDU的搜索结果上名列前茅。</p>', '', NULL, NULL, '2008-01-01 00:00:00', '2008-01-01 00:00:00'),
(4, 'chi', 5, '用户注册协议', '', '', '<p>八、 责任范围及责任限制 <br />\r\n&nbsp;&nbsp;&nbsp; （一） 本公司仅对本协议中列明的责任承担范围负责。 <br />\r\n&nbsp;&nbsp;&nbsp; （二） 您明确因交易所产生的任何风险应由您与交易对方承担。<br />\r\n&nbsp;&nbsp;&nbsp; （三） 支付宝用户信息是由用户本人自行提供的，本公司无法保证该信息之准确、及时和完整，您应对您的判断承担全部责任。 <br />\r\n&nbsp;&nbsp;&nbsp; （四） 本公司不对交易标的及本服务提供任何形式的保证，包括但不限于以下事项： <br />\r\n&nbsp;&nbsp;&nbsp; 1、本服务符合您的需求。<br />\r\n&nbsp;&nbsp;&nbsp; 2、本服务不受干扰、及时提供或免于出错。<br />\r\n&nbsp;&nbsp;&nbsp; 3、您经由本服务购买或取得之任何产品、服务、资讯或其他资料符合您的期望。<br />\r\n&nbsp;&nbsp;&nbsp; （五） 本服务之合作单位，所提供之服务品质及内容由该合作单位自行负责。 <br />\r\n&nbsp;&nbsp;&nbsp; （六） 您经由本服务之使用下载或取得任何资料，应由您自行考量且自负风险，因资料之下载而导致您电脑系统之任何损坏或资料流失，您应负完全责任。<br />\r\n&nbsp;&nbsp;&nbsp; （七） 您自本公司及本公司工作人员或经由本服务取得之建议和资讯，无论其为书面或口头形式，均不构成本公司对本服务之保证。</p>', '', '', NULL, '2009-05-12 18:28:22', '2009-05-15 11:55:59');
